const raw = $json.output || "";
const jsonMatch = raw.match(/{[\s\S]*}/);

if (!jsonMatch) {
  throw new Error("No JSON object found in output");
}

let parsed;
try {
  parsed = JSON.parse(jsonMatch[0]);
} catch (e) {
  throw new Error("Failed to parse JSON: " + e.message);
}

return [
  {
    json: {
      role: parsed.role,
      message: parsed.message
    }
  }
];

